import { legacy_createStore as createStore } from "redux";


export const store = createStore(manageStore)

// store = {
//     datafromDB: {}
// }




// action = {
//     type : 
//     payload :{       
//     }
// }



function manageStore(store = {
        datafromDB: {"account-create": NaN, "reason": ""}
    }
    , action){

        switch (action.type){
            case "settextfromDB":
                return { ...store, datafromDB : action.paylod}

            default :
                return store;
        }

}
